import {Box, List, ListItemButton, ListItemIcon, ListItemText, Toolbar, Typography} from "@mui/material";
import {ExpandLess, ExpandMore, Store as StoreIcon,} from '@mui/icons-material';
import {useEffect, useState} from "react";
import {menuAPI} from "./api/APIService";

function App() {
    /** 菜单的加载状态 默认加载中*/
    const [loadingMenus, setLoadingMenus] = useState(true);
    /** 菜单数据 */
    const [menus, setMenus] = useState([]);
    useEffect(() => {
        const loadMenus = async () => {
            try {
                const menusData = await menuAPI.getAll();
                //设置菜单数据
                setMenus(menusData);
            }catch (error) {

            }finally {
                /** 加载结束*/
                setLoadingMenus(false);
            }
        }
    })
  return (
    <Box sx={{display: 'flex',minHeight: '100vh',flexDirection: 'row'}}>
       <Box sx={{bgcolor:'grey.900',color:'grey.400',boxSizing:'border-box'}}>
           <Toolbar sx={{ my: 4 }}>
               <Box display="flex" alignItems="center">
                   <StoreIcon sx={{ color: 'primary.main', mr: 1, fontSize: 32 }} />
                   <Typography variant="h6" color="grey.50" sx={{ fontWeight: 'bold' }}>
                       Mini ERP
                   </Typography>
               </Box>
           </Toolbar>

           {/*构建菜单*/}
           <List sx={{px:2}}>
                <ListItemButton>
                    <ListItemIcon/>
                    <ListItemText>sssssssss</ListItemText>
                </ListItemButton>

               <ListItemButton>
                   <ListItemIcon/>
                   <ListItemText>sssssssss</ListItemText>
                   <ExpandMore />
                   <ListItemButton>
                       <ListItemIcon/>
                       <ListItemText>sssssssss</ListItemText>
                       <ExpandMore />
                   </ListItemButton>
               </ListItemButton>
           </List>
       </Box>
       <Box sx={{bgcolor:'red'}}>Test</Box>
    </Box>
  );
}

export default App;
