export const menuAPI = {
    /**
     * 获取所有菜单 http://localhost:8080/api/menus/all
     */
    getAll: async () => {
        try {
            const response = await fetch('/api/menus/all')
                .then(response => {
                    console.log('请求的 URL:', response.url);
                    console.log('HTTP 状态码:', response.status);
                    console.log('响应是否 OK:', response.ok);
                    if (!response.ok) {
                        throw new Error('网络请求失败');
                    }
                    return response.json();
                }).then(data => {
                    console.log('拿到数据啦！', data); // ✅ 这里才是真正的数据
                }).catch(error => {
                    console.error('请求出错:', error);
                });

            if (!response.ok) throw new Error('获取菜单失败');
            return await response.json();
        } catch (error) {
            console.error('API Error:', error);
            throw error;
        }
    },
}
