               <Box display="flex" alignItems="center">
                   <StoreIcon sx={{ color: 'primary.main', mr: 1, fontSize: 32 }} />
                   <Typography variant="h6" color="grey.50" sx={{ fontWeight: 'bold' }}>
                       Mini ERP
                   </Typography>
               </Box>
           </Toolbar>

           {/*构建菜单*/}
